                <input type="password" id="password" placeholder="Masukkan password Anda">
            </div>
            <div class="error" id="loginError">Email atau password salah</div>
            <button class="btn" id="loginBtn">Login</button>
        </div>
    </div>

    <!-- Header -->
    <header>
        <div class="logo" onclick="showLoginModal()">binndev</div>
        <div class="search-box">
            <input type="text" placeholder="Cari di binndev...">
        </div>
        <div class="header-icons" id="headerIcons">
            <button class="login-btn" onclick="showLoginModal()">Login</button>
        </div>
    </header>

