        // Handle like with real-time count
        function handleLike(element, postId) {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            const post = posts.find(p => p.id === postId);
            if (!post) return;

            // Toggle like status
            post.liked = !post.liked;
            post.likes += post.liked ? 1 : -1;

            // Update UI
            const likeBtn = element;
            likeBtn.classList.toggle('liked');

            // Update like count
            const likeCountElement = likeBtn.querySelector('.like-count');
            if (likeCountElement) {
                likeCountElement.textContent = post.likes.toLocaleString();
            } else {
                likeBtn.innerHTML = `‚ù§Ô∏è <span class="like-count">${post.likes.toLocaleString()}</span>`;
                if (post.liked) {
                    likeBtn.classList.add('liked');
                }
            }
        }

        // Handle comment like with real-time count
        function handleCommentLike(element, postId, commentId) {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            const post = posts.find(p => p.id === postId);
            if (!post) return;

            const comment = post.comments.find(c => c.id === commentId);
            if (!comment) return;

            // Toggle like status
            comment.liked = !comment.liked;
            comment.likes += comment.liked ? 1 : -1;

            // Update UI
            const likeBtn = element;
            likeBtn.classList.toggle('liked');

            // Update like count
            const likeCountElement = likeBtn.querySelector('.comment-like-count');
            if (likeCountElement) {
                likeCountElement.textContent = comment.likes;
            } else {
                likeBtn.innerHTML = `üëç <span class="comment-like-count">${comment.likes}</span>`;
                if (comment.liked) {
                    likeBtn.classList.add('liked');
                }
            }
        }

