        function handleCommentLike(element, postId, commentId) {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            const post = posts.find(p => p.id === postId);
            if (!post) return;

            const comment = post.comments.find(c => c.id === commentId);
            if (!comment) return;

            // Toggle like status
            comment.liked = !comment.liked;
            comment.likes += comment.liked ? 1 : -1;

            // Update UI
            const likeBtn = element;
            likeBtn.classList.toggle('liked');

            // Update like count
            const likeCountElement = likeBtn.querySelector('.comment-like-count');
            if (likeCountElement) {
                likeCountElement.textContent = comment.likes;
            } else {
                likeBtn.innerHTML = `üëç <span class="comment-like-count">${comment.likes}</span>`;
                if (comment.liked) {
                    likeBtn.classList.add('liked');
                }
            }
        }

        // Show delete confirmation
        function showDeleteConfirmation(postId) {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            const post = posts.find(p => p.id === postId);
            if (!post) return;

            // Check if current user is the author
            if (post.authorId !== currentUser.id) {
                successText.textContent = "Anda hanya bisa menghapus postingan Anda sendiri!";
                successMessage.classList.add('show');

                setTimeout(() => {
                    successMessage.classList.remove('show');
                }, 3000);
                return;
            }

            currentPostToDelete = postId;
            deleteModal.style.display = 'block';
        }

        // Confirm delete
        confirmDeleteBtn.addEventListener('click', function() {
            if (!currentPostToDelete) return;

            // Find index of post to delete
            const postIndex = posts.findIndex(p => p.id === currentPostToDelete);
            if (postIndex === -1) return;

            // Remove post
            posts.splice(postIndex, 1);

            // Update user's post count
            currentUser.posts -= 1;
            updateProfileUI(currentUser);

            // Reload current view
            if (document.querySelector('#profileLink').classList.contains('active')) {
                loadProfilePosts();
            } else {
                loadAllPosts();
            }

            // Hide modal
            deleteModal.style.display = 'none';
            currentPostToDelete = null;

            // Show success message
            successText.textContent = "Postingan berhasil dihapus!";
            successMessage.classList.add('show');

            setTimeout(() => {
                successMessage.classList.remove('show');
            }, 3000);
        });

        // Cancel delete
        cancelDeleteBtn.addEventListener('click', function() {
            deleteModal.style.display = 'none';
            currentPostToDelete = null;
