                </div>
            `;
            document.body.appendChild(dmListModal);

            // Close DM list
            dmListModal.querySelector('#closeDmListBtn').addEventListener('click', function() {
                document.body.removeChild(dmListModal);
            });
        }

        // Show DM modal
        function showDmModal(userId) {
            const user = users.find(u => u.id === userId);
            if (!user) return;

            // Close DM list if open
            const dmListModal = document.querySelector('.modal-overlay');
            if (dmListModal) {
                document.body.removeChild(dmListModal);
            }

            // Set recipient info
            currentDmRecipient = user;
            recipientAvatar.src = user.avatar;
            recipientName.textContent = user.name;
            recipientUsername.textContent = user.username;

            // Show DM modal
            dmModal.style.display = 'block';
        }

        // Send DM
        sendDmBtn.addEventListener('click', function() {
            if (!currentUser || !currentDmRecipient) {
                showLoginRequired();
                return;
            }

            const message = dmMessage.value.trim();
            if (!message) {
                successText.textContent = "Pesan tidak boleh kosong!";
                successMessage.classList.add('show');

                setTimeout(() => {
                    successMessage.classList.remove('show');
                }, 3000);
                return;
            }

            // In a real app, this would send the message to a server
            // For demo, just show success message
            successText.textContent = `Pesan terkirim ke ${currentDmRecipient.name}!`;
            successMessage.classList.add('show');

            setTimeout(() => {
                successMessage.classList.remove('show');
            }, 3000);

            // Reset and close
            dmMessage.value = '';
            dmModal.style.display = 'none';
            currentDmRecipient = null;
        });

        // Close DM modal
        closeDmBtn.addEventListener('click', function() {
            dmModal.style.display = 'none';
            currentDmRecipient = null;
        });

        // Show edit profile modal
        function showEditProfileModal() {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            // Create a simple edit modal dynamically
            const editModal = document.createElement('div');
            editModal.className = 'modal-overlay';
            editModal.style.display = 'flex';
            editModal.innerHTML = `
                <div class="modal" style="width: 400px;">
                    <h2>Edit Profil</h2>
                    <div class="input-group">
                        <label for="editName">Nama</label>
                        <input type="text" id="editName" value="${currentUser.name}">
                    </div>
                    <div class="input-group">
                        <label for="editUsername">@Username</label>
                        <input type="text" id="editUsername" value="${currentUser.username.replace('@', '')}">
                    </div>
                    <div class="input-group">
                        <label for="editBio">Bio</label>
                        <textarea id="editBio" style="height: 80px;">${currentUser.bio}</textarea>
                    </div>
                    <div class="input-group">
                        <label for="editAvatar">Foto Profil</label>
                        <select id="editAvatar" style="width: 100%; padding: 12px; border: 1px solid #dbdbdb; border-radius: 6px; font-size: 14px;">
                            ${profilePictures.map((pic, index) => `
                                <option value="${pic}" ${currentUser.avatar === pic ? 'selected' : ''}>Foto ${index + 1}</option>
                            `).join('')}
                        </select>
                    </div>
                    <div class="error" id="editError" style="display: none;">Terjadi kesalahan</div>
                    <button class="btn" id="saveEditBtn">Simpan Perubahan</button>
                    <button style="background: #efefef; color: #262626; margin-top: 10px;" id="cancelEditBtn">Batal</button>
                </div>
            `;
            document.body.appendChild(editModal);

            // Save edit
            editModal.querySelector('#saveEditBtn').addEventListener('click', function() {
                const name = editModal.querySelector('#editName').value.trim();
                const username = editModal.querySelector('#editUsername').value.trim();
                const bio = editModal.querySelector('#editBio').value.trim();
                const avatar = editModal.querySelector('#editAvatar').value;

                if (name && username) {
                    currentUser.name = name;
                    currentUser.username = `@${username}`;
                    currentUser.bio = bio;
                    currentUser.avatar = avatar;

                    // Update UI
                    updateProfileUI(currentUser);

                    // Remove modal
                    document.body.removeChild(editModal);

                    // Show success message
                    successText.textContent = "Profil berhasil diperbarui!";
                    successMessage.classList.add('show');

                    setTimeout(() => {
                        successMessage.classList.remove('show');
                    }, 3000);
                } else {
                    editModal.querySelector('#editError').style.display = 'block';
                }
            });

            // Cancel edit
            editModal.querySelector('#cancelEditBtn').addEventListener('click', function() {
                document.body.removeChild(editModal);
            });
        }

        // Show Explore modal
        function showExplore() {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            // Show explore modal
            exploreModal.style.display = 'block';

            // Set initial tab
            currentExploreTab = 'posts';
            updateExploreTabs();
            loadExploreContent();

            // Focus on search input
            exploreSearch.focus();
        }

        // Close Explore modal
        closeExplore.addEventListener('click', function() {
            exploreModal.style.display = 'none';
        });

        // Update Explore tabs
        function updateExploreTabs() {
            exploreTabs.forEach(tab => {
                tab.classList.remove('active');
                if (tab.getAttribute('data-tab') === currentExploreTab) {
                    tab.classList.add('active');
                }
            });
        }

        // Handle tab clicks
        exploreTabs.forEach(tab => {
            tab.addEventListener('click', function() {
                currentExploreTab = this.getAttribute('data-tab');
                updateExploreTabs();
                loadExploreContent();
            });
        });

        // Handle search input
        exploreSearch.addEventListener('input', function() {
            loadExploreContent();
        });

        // Load Explore content based on current tab and search query
        function loadExploreContent() {
            const query = exploreSearch.value.toLowerCase();

            if (currentExploreTab === 'posts') {
                loadExplorePosts(query);
            } else if (currentExploreTab === 'people') {
                loadExplorePeople(query);
            } else if (currentExploreTab === 'audio') {
                loadExploreAudio(query);
            }
        }

        // Load Explore Posts
        function loadExplorePosts(query) {
            let filteredPosts = posts;

            if (query) {
                filteredPosts = posts.filter(post => 
                    post.content.toLowerCase().includes(query) || 
                    post.author.toLowerCase().includes(query)
                );
            }

            exploreContent.innerHTML = `
                <div class="explore-results">
                    ${filteredPosts.slice(0, 20).map(post => `
                        <div class="explore-item" onclick="viewPost(${post.id})">
                            <img src="${post.image}" alt="Post">
                            <div class="explore-item-content">
                                <div class="explore-item-username">${post.author}</div>
                                <div class="explore-item-name">${post.location}</div>
                            </div>
                        </div>
                    `).join('')}
                </div>
            `;
        }

        // Load Explore People
        function loadExplorePeople(query) {
            let filteredUsers = users;

            if (query) {
                filteredUsers = users.filter(user => 
                    user.name.toLowerCase().includes(query) || 
                    user.username.toLowerCase().includes(query) ||
                    user.bio.toLowerCase().includes(query)
                );
            }

            exploreContent.innerHTML = `
                <div class="explore-results">
                    ${filteredUsers.map(user => `
                        <div class="explore-item" onclick="viewProfile(${user.id})">
                            <img src="${user.avatar}" alt="User">
                            <div class="explore-item-content">
                                <div class="explore-item-username">${user.name}</div>
                                <div class="explore-item-name">${user.username}</div>
                            </div>
                        </div>
                    `).join('')}
                </div>
            `;
        }

        // Load Explore Audio
        function loadExploreAudio(query) {
            let filteredAudio = audioTracks;

            if (query) {
                filteredAudio = audioTracks.filter(track => 
                    track.title.toLowerCase().includes(query) || 
                    track.artist.toLowerCase().includes(query)
                );
            }

            exploreContent.innerHTML = `
                ${filteredAudio.map(track => `
                    <div class="audio-player">
                        <img src="${track.cover}" alt="Album Cover">
                        <div class="audio-info">
                            <div class="audio-title">${track.title}</div>
                            <div class="audio-artist">${track.artist}</div>
                        </div>
                        <button class="play-btn">‚ñ∂</button>
                    </div>
                `).join('')}
            `;
        }

        // View Post (placeholder)
        function viewPost(postId) {
            successText.textContent = "Fitur detail postingan akan segera hadir!";
            successMessage.classList.add('show');

            setTimeout(() => {
                successMessage.classList.remove('show');
            }, 3000);
        }

        // View Profile (placeholder)
        function viewProfile(userId) {
            successText.textContent = "Fitur detail profil akan segera hadir!";
            successMessage.classList.add('show');

            setTimeout(() => {
                successMessage.classList.remove('show');
            }, 3000);
        }

        // Render a single post
        function renderPost(post, container, prepend = false) {
            const postElement = document.createElement('div');
            postElement.className = 'post';
            postElement.dataset.postId = post.id;

            const verifiedBadge = post.isVerified ? '<span class="verified-badge">‚úì</span>' : '';

            let imageHtml = '';
            if (post.image) {
                imageHtml = `<img src="${post.image}" alt="Post Image" class="post-img">`;
            }

            // Check if current user can delete this post
            const deleteBtnHtml = currentUser && post.authorId === currentUser.id ? 
                `<span class="delete-btn-post" onclick="showDeleteConfirmation(${post.id})">üóëÔ∏è</span>` : '';

            postElement.innerHTML = `
                <div class="post-header">
                    <img src="${post.authorAvatar}" alt="User">
                    <div>
                        <strong>${post.author}</strong>${verifiedBadge}<br>
                        <small>${post.location}</small>
                    </div>
                    ${deleteBtnHtml}
                </div>
                ${imageHtml}
                <div class="post-actions">
                    <span class="like-btn ${post.liked ? 'liked' : ''}" onclick="handleLike(this, ${post.id})">‚ù§Ô∏è <span class="like-count">${post.likes.toLocaleString()}</span></span>
                    <span class="share-btn" onclick="showShare(${post.id})">üì§</span>
                </div>
                <div class="post-caption">
                    <strong>${post.author}</strong> ${post.content}
                </div>
                <div class="post-time">${post.timestamp}</div>
                <div class="comments-section">
                    ${post.comments.map(comment => `
                        <div class="comment">
                            <img src="${comment.authorAvatar}" alt="User">
                            <div class="comment-content">
                                <span class="comment-author">${comment.author}</span> ${comment.content}
                            </div>
                            <div class="comment-actions">
                                <span class="comment-like-btn ${comment.liked ? 'liked' : ''}" onclick="handleCommentLike(this, ${post.id}, ${comment.id})">üëç <span class="comment-like-count">${comment.likes}</span></span>
                            </div>
                        </div>
                    `).join('')}
                    <div class="add-comment">
                        <input type="text" placeholder="Tambahkan komentar..." onkeypress="if(event.key==='Enter'){addComment(${post.id}, this)}">
                        <button onclick="addComment(${post.id}, this.previousElementSibling)">Kirim</button>
                    </div>
                </div>
            `;

            if (prepend) {
                container.insertBefore(postElement, container.firstChild);
            } else {
                container.appendChild(postElement);
            }
        }

        // Load all posts (Home)
        function loadAllPosts() {
            postsContainer.innerHTML = '';
            posts.forEach(post => {
                renderPost(post, postsContainer);
            });
        }

        // Load profile posts
        function loadProfilePosts() {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            postsContainer.innerHTML = '';
            const userPosts = posts.filter(post => post.authorId === currentUser.id);

            if (userPosts.length === 0) {
                postsContainer.innerHTML = '<div style="text-align: center; padding: 40px; color: #8e8e8e;">Belum ada postingan</div>';
            } else {
                userPosts.forEach(post => {
                    renderPost(post, postsContainer);
                });
            }
        }

        // Initialize
        loadAllPosts();

        // Set default profile (before login)
        const defaultUser = users[0];
        updateProfileUI(defaultUser);

        // Set up event listeners for suggestion follow buttons
        document.addEventListener('click', function(e) {
            if (e.target.classList.contains('follow-btn') || e.target.classList.contains('following-btn')) {
                const userId = parseInt(e.target.getAttribute('data-user-id'));
                const username = e.target.getAttribute('data-username');
                if (userId && username) {
                    toggleFollow(e.target, userId, username);
                }
            }
        });

        // Set up active link for home by default
        homeLink.classList.add('active');
    </script>
</body>
</html>
