        // Add comment
        function addComment(postId, commentInput) {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            const commentText = commentInput.value.trim();
            if (!commentText) return;

            const post = posts.find(p => p.id === postId);
            if (post) {
                const newComment = {
                    id: Date.now(), // Use timestamp as ID
                    author: currentUser.name,
                    authorAvatar: currentUser.avatar,
                    content: commentText,
                    likes: 0,
                    liked: false
                };

                post.comments.push(newComment);

                // Render the new comment
                const commentsSection = commentInput.closest('.comments-section');
                const commentElement = document.createElement('div');
                commentElement.className = 'comment';
                commentElement.innerHTML = `
                    <img src="${currentUser.avatar}" alt="User">
                    <div class="comment-content">
                        <span class="comment-author">${currentUser.name}</span> ${commentText}
                    </div>
                    <div class="comment-actions">
                        <span class="comment-like-btn" onclick="handleCommentLike(this, ${postId}, ${newComment.id})">üëç <span class="comment-like-count">0</span></span>
                    </div>
                `;
                commentsSection.insertBefore(commentElement, commentInput.closest('.add-comment'));

                // Clear input
                commentInput.value = '';

                // Add notification for the post author (if not the current user)
                if (post.authorId !== currentUser.id) {
                    addNotification({
                        id: Date.now(),
                        type: 'comment',
                        fromUser: currentUser,
                        toUserId: post.authorId,
                        postId: postId,
                        message: `${currentUser.name} mengomentari postingan Anda: "${commentText.substring(0, 50)}${commentText.length > 50 ? '...' : ''}"`,
                        time: 'baru saja',
                        read: false
                    });
                }

                // Show success message
                successText.textContent = "Komentar berhasil ditambahkan!";
                successMessage.classList.add('show');

                setTimeout(() => {
                    successMessage.classList.remove('show');
                }, 3000);
            }
        }

        // Toggle follow with real-time follower/following count update
        function toggleFollow(button, userId, username) {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            const targetUser = users.find(u => u.id === userId);
            if (!targetUser) return;

            // Check if currently following
            const isFollowing = currentUser.followingList && currentUser.followingList.includes(userId);

            if (isFollowing) {
                // Unfollow
                currentUser.followingList = currentUser.followingList.filter(id => id !== userId);
                targetUser.followers -= 1;
                currentUser.following -= 1;
                button.textContent = "Follow";
                button.className = "follow-btn";
                button.setAttribute('data-user-id', userId);
                button.setAttribute('data-username', username);

                // Show success message
                successText.textContent = `Berhenti mengikuti ${username}`;
            } else {
                // Follow
                if (!currentUser.followingList) {
                    currentUser.followingList = [];
                }
                currentUser.followingList.push(userId);
                targetUser.followers += 1;
                currentUser.following += 1;
                button.textContent = "Following";
                button.className = "following-btn";
                button.setAttribute('data-user-id', userId);
                button.setAttribute('data-username', username);

                // Add notification for the followed user
                addNotification({
                    id: Date.now(),
                    type: 'follow',
                    fromUser: currentUser,
                    toUserId: userId,
                    message: `${currentUser.name} mulai mengikuti Anda`,
                    time: 'baru saja',
                    read: false
                });

                // Show success message
                successText.textContent = `Mengikuti ${username}`;
            }

            // Update profile UI if viewing own profile
            if (document.querySelector('#profileLink').classList.contains('active')) {
                updateProfileUI(currentUser);
            }

            // Update the target user's UI if they are in suggestions
            const suggestionItems = document.querySelectorAll('.suggestion-item');
            suggestionItems.forEach(item => {
                const btn = item.querySelector('button');
                if (btn && btn.getAttribute('data-user-id') == userId) {
                    if (isFollowing) {
                        btn.textContent = "Follow";
                        btn.className = "follow-btn";
                    } else {
                        btn.textContent = "Following";
                        btn.className = "following-btn";
                    }
                    btn.setAttribute('data-user-id', userId);
                    btn.setAttribute('data-username', username);
                }
            });

            successMessage.classList.add('show');

            setTimeout(() => {
                successMessage.classList.remove('show');
            }, 3000);
        }

        // Show DM list (placeholder for now)
        function showDmList() {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            // Create a simple DM list modal
            const dmListModal = document.createElement('div');
            dmListModal.className = 'modal-overlay';
            dmListModal.style.display = 'flex';
            dmListModal.innerHTML = `
                <div class="modal" style="width: 400px; max-height: 80vh; overflow-y: auto;">
                    <h2>Pesan Langsung</h2>
                    <div style="margin: 20px 0; text-align: left;">
                        ${users.filter(u => u.id !== currentUser.id).map(user => `
