                successText.textContent = "Komentar berhasil ditambahkan!";
                successMessage.classList.add('show');

                setTimeout(() => {
                    successMessage.classList.remove('show');
                }, 3000);
            }
        }

        // Toggle follow
        function toggleFollow(button, username) {
            if (!currentUser) {
                showLoginRequired();
                return;
            }

            const isFollowing = followingList.includes(username);

            if (isFollowing) {
                // Unfollow
                followingList = followingList.filter(name => name !== username);
                button.textContent = "Follow";
                button.className = "follow-btn";
                button.setAttribute('data-username', username);
            } else {
                // Follow
                followingList.push(username);
                button.textContent = "Following";
                button.className = "following-btn";
                button.setAttribute('data-username', username);
            }

            // Show success message
            successText.textContent = isFollowing ? `Berhenti mengikuti ${username}` : `Mengikuti ${username}`;
            successMessage.classList.add('show');

            setTimeout(() => {
                successMessage.classList.remove('show');
