            const post = posts.find(p => p.id === postId);
            if (!post) return;

            // Check if user already liked this post
            const alreadyLiked = post.liked;

            // Toggle like status
            post.liked = !post.liked;
            post.likes += post.liked ? 1 : -1;

            // Update UI
            const likeBtn = element;
            likeBtn.classList.toggle('liked');

            // Update like count
            const likeCountElement = likeBtn.querySelector('.like-count');
            if (likeCountElement) {
                likeCountElement.textContent = post.likes.toLocaleString();
            } else {
                likeBtn.innerHTML = `‚ù§Ô∏è <span class="like-count">${post.likes.toLocaleString()}</span>`;
                if (post.liked) {
                    likeBtn.classList.add('liked');
                }
            }

            // Add notification for the post author (if not the current user)
            if (post.authorId !== currentUser.id && post.liked && !alreadyLiked) {
                addNotification({
                    id: Date.now(),
                    type: 'like',
                    fromUser: currentUser,
                    toUserId: post.authorId,
                    postId: postId,
                    message: `${currentUser.name} menyukai postingan Anda`,
                    time: 'baru saja',
                    read: false
                });
            }
        }

